// /MAT/meshStandardBuilder1/attribute1
varying float varying_v_POLY_attribute1_val;




#include <packing>
#include <uv_pars_fragment>
#include <map_pars_fragment>
#include <alphamap_pars_fragment>
#include <logdepthbuf_pars_fragment>
#include <clipping_planes_pars_fragment>

varying vec2 vHighPrecisionZW;

void main() {

	#include <clipping_planes_fragment>

	vec4 diffuseColor = vec4( 1.0 );



	// /MAT/meshStandardBuilder1/attribute1
	float v_POLY_attribute1_val = varying_v_POLY_attribute1_val;

	// /MAT/meshStandardBuilder1/round1
	float v_POLY_round1_val = sign(v_POLY_attribute1_val)*floor(abs(v_POLY_attribute1_val)+0.5);

	// /MAT/meshStandardBuilder1/floatToVec2_1
	vec2 v_POLY_floatToVec2_1_vec2 = vec2(v_POLY_round1_val, 130.0);

	// /MAT/meshStandardBuilder1/random1
	float v_POLY_random1_rand = rand(v_POLY_floatToVec2_1_vec2);

	// /MAT/meshStandardBuilder1/floatToVec3_1
	vec3 v_POLY_floatToVec3_1_vec3 = vec3(v_POLY_random1_rand, 1.0, 0.95);

	// /MAT/meshStandardBuilder1/hsvToRgb1
	vec3 v_POLY_hsvToRgb1_rgb = hsv2rgb(v_POLY_floatToVec3_1_vec3);

	// /MAT/meshStandardBuilder1/output1
	diffuseColor.xyz = v_POLY_hsvToRgb1_rgb;
	float POLY_metalness = 1.0;
	float POLY_roughness = 1.0;




	#if DEPTH_PACKING == 3200

		diffuseColor.a = opacity;

	#endif

	#include <map_fragment>
	#include <alphamap_fragment>
	#include <alphatest_fragment>

	#include <logdepthbuf_fragment>

	// Higher precision equivalent of gl_FragCoord.z. This assumes depthRange has been left to its default values.
	float fragCoordZ = 0.5 * vHighPrecisionZW[0] / vHighPrecisionZW[1] + 0.5;

	#if DEPTH_PACKING == 3200

		gl_FragColor = vec4( vec3( 1.0 - fragCoordZ ), opacity );

	#elif DEPTH_PACKING == 3201

		gl_FragColor = packDepthToRGBA( fragCoordZ );

	#endif

}

